const Header = (props) => {
  return (
    <h1>{props.course}</h1>
  )
}

const Content = (props) => {
  return (
    <div>
    <p>
    {props.part1} {props.exercises1}
    </p>
    <p>
    {props.part2} {props.exercises2}
    </p>
    <p>
    {props.part3} {props.exercises3}
    </p>
    </div>
  )
}

const Total = (props) => {
  return (
    <p>
        {props.exercises}
    </p>
  )
}


const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }


  return (
    <div>
      <Header course={course}/>
      <Content part1={part1.name}  exercises1={part1.exercises} part2={part2.name} exercises={part2.exercises} part3={part3.name} exercises={part3.exercises} />
      <Total exercises={part1.exercises + part2.exercises + part3.exercises} />

    </div>
  )
}

export default App
